import React, {Component, Fragment} from 'react'
import ReactDOM from 'react-dom'

class ComponentWithProps extends Component {
    // 리액트할 때(컴포넌트 상속받을 때) 생성자 생성(+부모생성자 호출) 필수!
    constructor(props) { //<ComponentWithProps value="Hello" asdf="World"/> 속성(value, asdf)들을 생성자를 통해 전달함
        super(props) //부모생성자 호출

        console.log(props)

        this.props.value = "Modify" //수정이 불가능하고, 하면 안된다.
    }

    render() {
        return (
            <>
                {/* 생성자를 통해 얻은 props를 this를 통해 접근한다.*/}
                <p>{this.props.asdf.toString()}</p>
            </>
        )
    }
}

// <ComponentWithProps value="Hello" asdf="World"/> => 속성과 속성값 해주기
ReactDOM.render(<ComponentWithProps value="Hello" asdf="World"/>, document.getElementById("root"))
