import React, {Component, Fragment} from 'react'
import ReactDOM from 'react-dom'

class ComponentWithProps extends Component {
    // 리액트할 때(컴포넌트 상속받을 때) 생성자 생성(+부모생성자 호출) 필수!
    constructor(props) {
        super(props) //부모생성자 호출

        console.log(props)
    }

    render() {
        return (
            <>
                <p>{this.props.value.toString()}</p> 
            </>
        )
    }
}

// <ComponentWithProps value="Hello" asdf="World"/> => 속성과 속성값 해주기
ReactDOM.render(<ComponentWithProps value="Hello" asdf="World"/>, document.getElementById("root"))
