import React from 'react'
import ReactDOM from 'react-dom'

const name = 'Josh Perez'
// 변수 내용 삽입 가능
const element = <h1>Hello, {name}</h1>

const lst = [100, 200, 300] //배열
const person = { //객체
    name: 'John',
    age: 20
}
function double(value) { //함수
    return value * 2
}

const JSXwithExpressions = (
    <h1>
        {/*중괄호를 하고 주석달기*/}
        {/* 밑에 처럼 변수, 배열 접근 가능 */}
        {lst[0]}
        &nbsp;{person.name}
        &nbsp;{person.age}
        &nbsp;{2 + 2}
        &nbsp;{person.name.toUpperCase()}
        &nbsp;{person.name.length}
        &nbsp;{double(person.age)}
    </h1>)

const age = 20
const conditionalElement = (
    <>{age >= 20 ? <div>성인</div> : <div>미성년자</div>}</>
)



ReactDOM.render(conditionalElement, document.getElementById("root"))
