import React, { Component } from 'react'
import ReactDOM from 'react-dom'

// onclick하면 div생성과 삭제
class SingleAccordion extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: '제목',
            content: '내용',
            expanded: false
        }
    }
    render() {
        return(
            <div style={ {border: '2px solid black', backgroundColor: 'lightgray', margin: '20px', padding: '20px'}}
                 onClick={ this.state.expanded ? this.setState(state => {return{expanded: false}}) : this.setState(state => {return{expanded: true}}) }
            >제목</div>

        );
    }
}
ReactDOM.render(<SingleAccordion />, document.getElementById("root"))
