// 다음 모듈 불러오기 구문은 매 예제마다 삽입
import React, {Component, Fragment} from 'react'
import ReactDOM from 'react-dom'

// 클래스 기반 컴포넌트 정의
class MyComponent extends Component { // 2줄에 있는 중괄호에 있는 component지우고 React.Component로 써도 됨
    // 렌더 함수 정의
    render() {
        // JSX를 이용하여 컴포넌트에서 보여줄 내용을 정의
        return (
            // 여러개의 태그를 쓰더라도 마지막에 하나의 태그로 감싸야함 => 효율적으로 관리하기 위해
            // import를 하지않으면 React.Fragment로 써야함
            // 안그리고 싶으면 Fragment로 쓰고 상관없으면 <> 이렇게 쓴다
            <>
                <h1>Title</h1>
                <div>My Component</div>
            </>
        )
    }
}

ReactDOM.render(<MyComponent />, document.getElementById("root"))
